import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import NexusJSON from './abi/Nexus.json'

const fetchJobs = async () => {
  console.log(process.env.ETH_ADDR);
  const web3 = new Web3(new Web3.providers.WebsocketProvider(process.env.ETH_ADDR));
  const contractAddress = "0x94D387F50569200aDACFd903345D077ef6ABcE11";
  const contract = new web3.eth.Contract(NexusJSON.abi,contractAddress);
  console.log(contract);
  const lastJob = await contract.methods.lastJobID().call();
  console.log(lastJob);
}

fetchJobs();

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
