const ethereum = window.ethereum;

const login = async () => {
    const accounts = await ethereum.request({ method: 'eth_requestAccounts' });
    console.log(accounts);
    this.setState({ account: accounts[0] })
}

// module.exports = {
//     login
// }

export default { 
    login
}
